package May_15;

public class For_pos_neg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1; i<=100; i++) {
			if(i>=0) {
				System.out.println("positve");
			}else if(i<=0)
			{
				System.out.println("negative");
			}
			else
			{
				System.out.println("invalide");
			}
		}
		
		

	}

}
